import React, { useEffect, useRef } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

import pp_img from '../img/pp.jpg'
import school_svg from '../svg/education.svg'
import location_svg from '../svg/location.svg'
import persona from '../svg/persona.svg'



gsap.registerPlugin(ScrollTrigger);

const About = () =>{

    const textRef = useRef(null);

    useEffect(() => {
        gsap.from(textRef.current, {
          opacity: 0,
          y: 50,
          duration: 1,
          ease: "power2.out",
          scrollTrigger: {
            trigger: textRef.current,
            start: "top 80%", // Démarrer lorsque le haut de l'élément atteint 80% de la hauteur de la fenêtre
            end: "bottom 60%", // Fin de l'animation lorsque le bas atteint 60%
            toggleActions: "play none none reverse",
          },
        });
      }, []);

    return (
        <div className="about" id="about">
            <img id="pp" src={pp_img}/>
            <div className='about_text_section'>
            <p className='about-content' ref={textRef}>Créateur numérique, passionné par la résolution de problèmes alliant technologie, créativité et analyse, j’évolue entre développement web et audiovisuel pour concevoir des solutions innovantes et esthétiques. </p>
            <div className='personal_infos'>
                    <div>
                        <img src={school_svg}/>
                        <p>Ingénieur IMAC</p>
                    </div>
                    <div>
                    <img src={persona}/>
                        <p>22 Ans</p>
                    </div>
                    <div>
                        <img src={location_svg}/>
                        <p>Paris, France</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default About;
