import React, { useRef, useEffect, useState } from "react";
import data from '../data/data.json';

const Split1 = () => {
    return (
        <div className="split1">
            <span></span>
            <p>Cliquez pour découvrir</p>
            <span></span>
        </div>
    );
};

const Menu_page_item = ({ titre }) => {
    return (
        <div className='menu_page_item'>
            <span></span>
            <p>{titre}</p>
        </div>
    );
};

const Menu_page = () => {
    const menuRef = useRef(null);
    const [scrollY, setScrollY] = useState(0);
    const [isVisible, setIsVisible] = useState(false); // État pour savoir si le composant est visible

    // Récupérer les sections depuis le JSON
    const sections = data.fr.sections;

    // Création des refs pour chaque carrousel
    const carouselRefs = useRef(sections.map(() => React.createRef()));

    // Gestion du scrollY pour déplacer les carrousels seulement si le composant est visible
    useEffect(() => {
        const handleScroll = () => {
            if (isVisible) { // Ne mettre à jour scrollY que si le composant est visible
                setScrollY(window.scrollY);
            }
        };

        window.addEventListener('scroll', handleScroll);

        // Nettoyage de l'événement de scroll à la destruction du composant
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [isVisible]); // L'effet dépend de isVisible

    useEffect(() => {
        // Initialiser l'IntersectionObserver pour détecter la visibilité du composant
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    // Le composant Menu_page est visible, on active la logique de défilement
                    setIsVisible(true);
                } else {
                    // Le composant Menu_page est hors de l'écran, on désactive le défilement
                    setIsVisible(false);
                }
            },
            { threshold: 0.5 } // Déclenche lorsque 50% du composant est visible
        );

        if (menuRef.current) {
            observer.observe(menuRef.current); // Observer le composant Menu_page
        }

        // Nettoyage de l'observer lors du démontage du composant
        return () => {
            if (menuRef.current) {
                observer.unobserve(menuRef.current);
            }
        };
    }, []);

    useEffect(() => {
        // Appliquer un décalage horizontal basé sur scrollY à chaque carrousel
        carouselRefs.current.forEach((ref, index) => {
            if (ref.current) {
                const offset = index * 100; // Crée un effet de décalage en fonction de l'index
                const translateXValue = -scrollY + offset;
                ref.current.style.transform = `translateX(${translateXValue}px)`;
            }
        });
    }, [scrollY, sections]); // L'effet se déclenche à chaque changement de scrollY ou sections

    const handleMouseOver = (index) => {
        // Mettre opacité à 0.7 pour tous les carrousels sauf celui survolé
        carouselRefs.current.forEach((ref, i) => {
            if (ref.current && i !== index) {
                ref.current.style.opacity = '0.7';
            }
        });
    };

    const handleMouseOut = () => {
        // Rétablir l'opacité à 1 pour tous les carrousels
        carouselRefs.current.forEach(ref => {
            if (ref.current) {
                ref.current.style.opacity = '1';
            }
        });
    };

    useEffect(() => {
        // Ajouter les événements de survol et de sortie pour chaque carrousel
        carouselRefs.current.forEach((ref, index) => {
            if (ref.current) {
                ref.current.addEventListener('mouseover', () => handleMouseOver(index));
                ref.current.addEventListener('mouseout', handleMouseOut);
            }
        });

        // Nettoyage des événements lors du démontage du composant
        return () => {
            carouselRefs.current.forEach((ref, index) => {
                if (ref.current) {
                    ref.current.removeEventListener('mouseover', () => handleMouseOver(index));
                    ref.current.removeEventListener('mouseout', handleMouseOut);
                }
            });
        };
    }, [sections]);

    return (
        <div className="menu_page" ref={menuRef}>
            <Split1 />
            {sections.map((section, index) => (
                <React.Fragment key={section.titre}>
                    <div 
                        className="carrousel" 
                        ref={carouselRefs.current[index]} // Référence assignée
                        id={section.titre}
                    >
                        {Array(12).fill(null).map((_, i) => (
                            <Menu_page_item key={i} titre={section.titre} />
                        ))}
                    </div>
                    {index < sections.length - 1 && <span className="split2" />}
                </React.Fragment>
            ))}
            <Split1 />
        </div>
    );
};

export default Menu_page;







