import { gsap } from "gsap";

import pp_img from '../img/pp.jpg'
import school_svg from '../svg/education.svg'
import location_svg from '../svg/location.svg'
import persona from '../svg/persona.svg'


// gsap.fromTo('.about', 
//     { opacity: 0, y: 50 }, 
//     { opacity: 1, y: 0, duration: 1, ease: 'power2.out', scrollTrigger: {
//         trigger: '.about',
//         start: 'top 80%',
//         end: 'bottom 20%',
//         toggleActions: 'play none none none'
//     }}
// );

const About = () =>{

    useEffect(() => {
        // Divise le texte en mots
        const splitText = new SplitType("#animated-text", { types: "words" });

        gsap.fromTo(
            splitText.words, 
            { opacity: 0, y: 10 }, 
            { 
                opacity: 1, 
                y: 0, 
                duration: 0.4, 
                stagger: 0.1, // Chaque mot apparaît avec un léger décalage
                ease: "power2.out",
                scrollTrigger: {
                    trigger: "#animated-text",
                    start: "top 80%",
                    toggleActions: "play none none none"
                }
            }
        );
    }, []);
    return (
        <div className="about" id="about">
            <img id="pp" src={pp_img}/>
            <div className='about_text_section'>
            <h2>Lorem ipsum de fou, trop cool la vie j'aime les chocolats !! Lorem ipsum de fou, trop cool la vie j'aime les chocolats !! </h2>
            <div className='personal_infos'>
                    <div>
                        <img src={school_svg}/>
                        <p>Ingénieur IMAC</p>
                    </div>
                    <div>
                    <img src={persona}/>
                        <p>22 Ans</p>
                    </div>
                    <div>
                        <img src={location_svg}/>
                        <p>Paris, France</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default About;
