import React, { useEffect, useRef } from "react";

export const Test = () => {
    const marqueeContentRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
      const marqueeContent = marqueeContentRef.current;
      if (marqueeContent) {
        const clone = marqueeContent.cloneNode(true);
        marqueeContent.parentElement?.appendChild(clone);
      }
    }, []);

    const containerStyle = {
      minHeight: "60px",
      background: "linear-gradient(to right, var(--goldgradient-dark), var(--goldgradient-light))",
      display: "flex",
      alignItems: "center",
      overflow: "hidden",
      gap: "5rem",
      borderRadius: "16px"
    };

    const contentStyle = {
      display: "flex",
      alignItems: "center",
      gap: "5rem",
      color: "var(--grey-600)"
    };

    const largeTextStyle = {
      fontWeight: 800,
      fontSize: "30px",
      verticalAlign: "middle"
    };

    const specialFontStyle = {
      fontFamily: "PPNeueBit",
      fontSize: "40px"
    };

    return (
      <div style={containerStyle} className="marquee">
        <div style={contentStyle} ref={marqueeContentRef}>
          <div>
            <span style={largeTextStyle}>Parraine</span>{" "}
            <span style={specialFontStyle}>un ami</span>
          </div>
          <div>
            <span style={largeTextStyle}>Gagne des jours</span>{" "}
            <span style={specialFontStyle}>gratuits</span>
          </div>
          <div>
            <span style={largeTextStyle}>Parraine</span>{" "}
            <span style={specialFontStyle}>un ami</span>
          </div>
          <div>
            <span style={largeTextStyle}>Gagne des jours</span>{" "}
            <span style={specialFontStyle}>gratuits</span>
          </div>
          <div>
            <span style={largeTextStyle}>Parraine</span>{" "}
            <span style={specialFontStyle}>un ami</span>
          </div>
          <div>
            <span style={largeTextStyle}>Gagne des jours</span>{" "}
            <span style={specialFontStyle}>gratuits</span>
          </div>
        </div>
      </div>
    );
  };
