import { useEffect, useState } from 'react';


import Menu from './Menu';
import Footer from './Footer';

import arrow from '../svg/arrow.svg';

import data from '../data/data.json';



const BannerTitleContent = () => {
    return(
        <div className='bannerTitleContent'>
            <span></span>
            <p>Développement</p>
        </div>
    );
}; 

const BannerTitle = ({ translateClass }) => {
    const nb_components = 5;
    const [components, setComponents] = useState(
        Array.from({ length: nb_components }, (_, index) => ({
            id: Date.now() + index,
            element: <BannerTitleContent key={Date.now() + index} />
        }))
    );

    useEffect(() => {
        const interval = setInterval(() => {
            setComponents(prevComponents => {
                const newComponent = {
                    id: Date.now(),
                    element: <BannerTitleContent key={Date.now()} />
                };

                return [newComponent, ...prevComponents.slice(0, -1)];
            });
        }, 120000); // 120 secondes = 120000 millisecondes

        // Nettoyer l'intervalle quand le composant est démonté
        return () => clearInterval(interval);
    }, []);

    return (
        <div className={`bannerTitle ${translateClass}`}>
            {components.map(comp => comp.element)}
        </div>
    );
};

const Category = () => {
    return(
        <div className='category'>
            <p>Vue.js</p>
        </div>
    ); 
}; 

const ProjectBanner = () => {
    return (
        <div className='project_banner'>
            <div className='titles_page'>
                <BannerTitle translateClass="translateTitle1"/>
                <BannerTitle translateClass="translateTitle2"/>
                <BannerTitle translateClass="translateTitle1"/>
            </div>
            <div className='categories_container'>
                <div className='categories'>
                    <Category/>
                    <Category/>
                    <Category/>
                    <Category/>
                </div>
                <div>
                    <a href="#fr"><img 
                    id="arrowHome" 
                    src={arrow} 
                    alt="scroll down" 
                    /></a>
                </div>

            </div>

        </div>
    ); 
}; 

const ProjectPage = () => {
    return (
        <div>
            <Menu />
            <ProjectBanner />
            <Footer />
        </div>
    );
};

export default ProjectPage;
