import React, { useEffect, useRef, useState } from 'react';
import data from '../data/data.json';

const Split1 = () => {
    return (
        <div className="split1">
            <span></span>
            <p>Cliquez pour découvrir</p>
            <span></span>
        </div>
    );
};

const Menu_page_item = ({ titre }) => {
    return (
        <div className='menu_page_item'>
            <span></span>
            <p>{titre}</p>
        </div>
    );
};

const Menu_page = () => {
    const menuRef = useRef(null);

    // Récupérer les sections depuis le JSON
    const sections = data.fr.sections;

    // Création des refs en utilisant un objet
    const carouselRefs = useRef(
        Object.fromEntries(sections.map(section => [section.titre, { current: null }]))
    );

    const [isVisible, setIsVisible] = useState(false);

    // Vérifier si le composant Menu_page est visible dans la fenêtre
    useEffect(() => {
        const handleScroll = () => {
            if (menuRef.current) {
                const rect = menuRef.current.getBoundingClientRect();
                setIsVisible(rect.top >= 0 && rect.bottom <= window.innerHeight);
            }
        };

        window.addEventListener('scroll', handleScroll);
        handleScroll(); // Vérifier immédiatement au cas où le composant serait déjà visible.

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    // Déplacer les carrousels de manière alternée lors du scroll
    useEffect(() => {
        if (isVisible) {
            const handleScrollY = () => {
                const scrollY = window.scrollY;

                sections.forEach((section, index) => {
                    const carousel = carouselRefs.current[section.titre].current;
                    if (carousel) {
                        // Déplacement alterné à gauche et à droite
                        const direction = index % 2 === 0 ? -1 : 1;
                        const translateValue = direction * (scrollY * 0.1);
                        carousel.style.transform = `translateX(${translateValue}px)`;
                        carousel.style.transition = 'transform 0.1s ease-out';
                    }
                });
            };

            window.addEventListener('scroll', handleScrollY);
            return () => {
                window.removeEventListener('scroll', handleScrollY);
            };
        }
    }, [isVisible, sections]);

    return (
        <div className="menu_page" ref={menuRef}>
            <Split1 />
            {sections.map((section, index) => (
                <React.Fragment key={section.titre}>
                    <div 
                        className="carrousel" 
                        ref={el => carouselRefs.current[section.titre].current = el}
                        id={section.titre}
                    >
                        {Array(12).fill(null).map((_, i) => (
                            <Menu_page_item key={i} titre={section.titre} />
                        ))}
                    </div>
                    {index < sections.length - 1 && <span className="split2" />}
                </React.Fragment>
            ))}
            <Split1 />
        </div>
    );
};

export default Menu_page;
















