import { useEffect, useRef, useState } from 'react';

const Projects = ({section}) => {
    const [activeProject, setActiveProject] = useState(null);
    const projectRefs = useRef([]);

    useEffect(() => {
        projectRefs.current = projectRefs.current.slice(0, section.projets.length);

        const options = {
            root: null,
            rootMargin: '0px',
            threshold: 0.5  // Élément considéré visible quand 50% est visible
        };

        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    const projectIndex = projectRefs.current.findIndex(
                        ref => ref === entry.target
                    );
                    setActiveProject(projectIndex);
                }
            });
        }, options);

        // Observer chaque projet
        projectRefs.current.forEach((ref) => {
            if (ref) observer.observe(ref);
        });

        return () => {
            projectRefs.current.forEach((ref) => {
                if (ref) observer.unobserve(ref);
            });
        };
    }, [section.projets.length]);

    return (
        <div className="projects" id="projects">
            <div className="projects-summary">
                <span></span>
                <div>
                    {section.projets.map((projet, index) => (
                        <p 
                            key={index}
                            style={{ 
                                opacity: activeProject === index ? 1 : 0.7,
                                transition: 'opacity 0.3s ease'
                            }}
                        >
                            {projet.titre_projet}
                        </p>
                    ))}
                </div>
            </div>
            <div className="projects-list">
                {section.projets.map((project, index) => (
                    <div 
                        key={index}
                        ref={el => projectRefs.current[index] = el}
                    >
                        <Project 
                            project={project} 
                            titreSection={section.titre}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Projects;
