import w_plane from "../svg/w_plane.svg";
import arrow from "../svg/arrow.svg";

const Home = () => {
  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      setScrollPosition(scrollY);
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // Calcul de la rotation pour le looping
  const planeStyle = {
    transform: `rotate(${scrollPosition % 360}deg) translateY(${scrollPosition / 5}px)`,
    transition: "transform 0.1s ease-out",
  };

  return (
    <div className="home">
      <span className="blur_point" id="orange_blur_point"></span>
      <span className="blur_point" id="blue_blur_point"></span>

      <div className="home-header">
        <h1>Michel</h1>
        <p className="subtitle">INGÉNIERIE CRÉATIVE</p>
      </div>

      {/* Ajout du style dynamique pour le looping */}
      <img
        id="planeHome"
        src={w_plane}
        alt="Airplane illustration"
        style={planeStyle}
      />
      <a href="#about">
        <img id="arrowHome" src={arrow} alt="scroll down" />
      </a>
    </div>
  );
};

export default Home;
