import { useEffect } from "react";
import w_plane from "../svg/w_plane.svg";
import arrow from "../svg/arrow.svg";

const Home = () => {

  useEffect(() => {

    const plane = document.getElementById('planeHome'); 


    const handleScroll = () => {
      if(plane){
        const plane_x_translate = window.scrollY * 0.5;
        // Augmente l'amplitude de la sinusoïde pour un looping plus prononcé
        const plane_y_translate = Math.sin(window.scrollY * 0.01) * 150;
        // Ajoute une rotation qui suit le mouvement du looping
        const plane_rotation = Math.sin(window.scrollY * 0.01) * 360;

        plane.style.transform = `translate(${plane_x_translate}px, ${plane_y_translate}px)`;

      }

    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);



  return (
    <div className="home">

      <span className="blur_point" id="orange_blur_point"></span>
      <span className="blur_point" id="blue_blur_point"></span>

      <div className="home-header">
        <h1>Tancrède Gorand</h1>
        <p className="subtitle">INGÉNIERIE CRÉATIVE</p>
      </div>

      <img id="planeHome" src={w_plane} alt="Airplane illustration" />
      <a href="#about"><img 
        id="arrowHome" 
        src={arrow} 
        alt="scroll down" 
      /></a>
    </div>
  );
};

export default Home;
