import { useEffect } from "react";
import w_plane from "../svg/w_plane.svg";
import arrow from "../svg/arrow.svg";

const Home = () => {

  useEffect(() => {
    const plane = document.getElementById('planeHome');

    const handleScroll = () => {
        if(plane) {
            const scrollProgress = window.scrollY * 0.003; // Ralentit le mouvement

            // Paramétric equations pour créer la forme de boucle
            const x = scrollProgress * 200;
            const y = (Math.sin(scrollProgress * 2) * 150) + 
                     (Math.cos(scrollProgress) * 100);

            // Calcul de la rotation pour suivre la trajectoire
            const rotation = (Math.atan2(
                Math.cos(scrollProgress * 2) * 300 + Math.sin(scrollProgress) * 100,
                200
            ) * 180 / Math.PI) + 90;

            plane.style.transform = `translate(${x}px, ${y}px) rotate(${rotation}deg)`;
        }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
        window.removeEventListener("scroll", handleScroll);
    };
}, []);

  return (
    <div className="home">

      <span className="blur_point" id="orange_blur_point"></span>
      <span className="blur_point" id="blue_blur_point"></span>

      <div className="home-header">
        <h1>Tancrède Gorand</h1>
        <p className="subtitle">INGÉNIERIE CRÉATIVE</p>
      </div>

      <img id="planeHome" src={w_plane} alt="Airplane illustration" />
      <a href="#about"><img 
        id="arrowHome" 
        src={arrow} 
        alt="scroll down" 
      /></a>
    </div>
  );
};

export default Home;
