import React, { useRef, useEffect, useState } from 'react';
import data from '../data/data.json';

const Split1 = () => {
    return (
        <div className="split1">
            <span></span>
            <p>Cliquez pour découvrir</p>
            <span></span>
        </div>
    );
};

const Menu_page_item = ({ titre }) => {
    return (
        <div className='menu_page_item'>
            <span></span>
            <p>{titre}</p>
        </div>
    );
};

const Menu_page = () => {
    const menuRef = useRef(null);

    // Récupérer les sections depuis le JSON
    const sections = data.fr.sections;

    // Création des refs pour chaque carrousel avec un index unique
    const carouselRefs = useRef(
        sections.reduce((acc, section, index) => {
            acc[index] = { current: null }; // Utilisation de l'index comme clé unique
            return acc;
        }, {})
    );

    // Utilisation d'IntersectionObserver pour suivre la visibilité du composant
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                setIsVisible(entry.isIntersecting); // Mise à jour de la visibilité
            },
            {
                threshold: 0.5 // Le carrousel doit être à 50% visible pour être considéré comme visible
            }
        );
        if (menuRef.current) {
            observer.observe(menuRef.current); // Observer le composant
        }

        return () => {
            if (menuRef.current) {
                observer.unobserve(menuRef.current);
            }
        };
    }, []);

    // Fonction pour animer les carrousels en fonction du scrollY
    const handleScroll = () => {
        if (!isVisible) return; // Ne pas déplacer les carrousels si le composant n'est pas visible

        sections.forEach((section, index) => {
            const carousel = carouselRefs.current[index].current;
            if (carousel) {
                // Calculer la translation en fonction du scroll
                const scrollPosition = window.scrollY;
                const offset = scrollPosition * 0.1; // Ajuste la vitesse de défilement ici
                carousel.style.transform = `translateX(${offset}px)`;
            }
        });
    };

    // Ajouter un écouteur d'événements pour le scroll
    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [isVisible]); // Re-créer l'écouteur lorsque la visibilité change

    return (
        <div className="menu_page" ref={menuRef}>
            <Split1 />
            {sections.map((section, index) => (
                <React.Fragment key={section.titre}>
                    <div
                        className="carrousel"
                        ref={el => carouselRefs.current[index].current = el} // Utilisation de l'index pour la ref unique
                        id={section.titre}
                    >
                        {Array(12).fill(null).map((_, i) => (
                            <Menu_page_item key={i} titre={section.titre} />
                        ))}
                    </div>
                    {index < sections.length - 1 && <span className="split2" />}
                </React.Fragment>
            ))}
            <Split1 />
        </div>
    );
};

export default Menu_page;

