import { useEffect, useRef } from 'react';
import Project from './Project';

const Projects = ({ section }) => {
    const projectRefs = useRef([]);

    useEffect(() => {
        const observer = new IntersectionObserver(
            (entries) => {
                entries.forEach((entry, index) => {
                    if (entry.isIntersecting) {
                        console.log(`Projet visible à l'écran : index ${entry.target.dataset.index}`);
                    }
                });
            }
        );

        projectRefs.current.forEach((el) => {
            if (el) observer.observe(el);
        });

        return () => {
            projectRefs.current.forEach((el) => {
                if (el) observer.unobserve(el);
            });
        };
    }, [section.projets]);

    return (
        <div className='projects' id='projects'>
            <div className='projects-summary'>
                <span></span>
                <div>
                    {section.projets.map((projet, index) => (
                        <p key={index}>{projet.titre_projet}</p>
                    ))}
                </div>
            </div>
            <div className='projects-list'>
                {section.projets.map((project, index) => (
                    <div key={index} ref={(el) => (projectRefs.current[index] = el)} data-index={index}>
                        <Project project={project} titreSection={section.titre} />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Projects;
