import { useEffect } from "react";
import w_plane from "../svg/w_plane.svg";
import arrow from "../svg/arrow.svg";

const Home = () => {

  useEffect(() => {
    const plane = document.getElementById('planeHome');

    const normaliseScroll = (scrollY) => {
        return scrollY / window.innerHeight;
    };

    // Points de contrôle pour la position X
    const controlPointsX = [0, 100, 300, 500, 600]; // Exemple d'unités en px
    const tValues = [0, 0.25, 0.5, 0.75, 1]; // Correspondance normalisée pour t (0 à 1)

    // Plage de t pour mobile
    const tMin = -150;
    const tMax = 428;

    // Fonction x_translate
    const x_translate = (t) => {
      // Normaliser t entre 0 et 1
      const tNormalized = (t - tMin) / (tMax - tMin);

      // Limiter tNormalisé à la plage [0, 1]
      if (tNormalized < 0) return controlPointsX[0];
      if (tNormalized > 1) return controlPointsX[controlPointsX.length - 1];

      // Calculer x en utilisant l'interpolation linéaire
      for (let i = 0; i < tValues.length - 1; i++) {
        if (tNormalized >= tValues[i] && tNormalized <= tValues[i + 1]) {
          const tStart = tValues[i];
          const tEnd = tValues[i + 1];
          const xStart = controlPointsX[i];
          const xEnd = controlPointsX[i + 1];

          // Interpolation linéaire
          const progress = (tNormalized - tStart) / (tEnd - tStart);
          return xStart + progress * (xEnd - xStart);
        }
      }
    };

    const calculateXTranslate = (normalizedScroll) => {
        const screenWidth = window.innerWidth;
        const startPosition = -150;
        const totalDistance = screenWidth + 250;
        return startPosition + (normalizedScroll * totalDistance);
    };

    const handleScroll = () => {
        const normalizedScrollPosition = normaliseScroll(window.scrollY);
        const xPosition = calculateXTranslate(normalizedScrollPosition);  
        const xFinalPosition = x_translate(xPosition); 



        console.log(xFinalPosition);
        plane.style.transform = `translate(${xFinalPosition}px)`;
    };


    handleScroll();

    window.addEventListener("scroll", handleScroll);
    window.addEventListener("resize", handleScroll);

    return () => {
        window.removeEventListener("scroll", handleScroll);
        window.removeEventListener("resize", handleScroll);
    };
}, []);







  return (
    <div className="home">

      <span className="blur_point" id="orange_blur_point"></span>
      <span className="blur_point" id="blue_blur_point"></span>

      <div className="home-header">
        <h1>Tancrède Gorand</h1>
        <p className="subtitle">INGÉNIERIE CRÉATIVE</p>
      </div>

      <img id="planeHome" src={w_plane} alt="Airplane illustration" />
      <a href="#about"><img 
        id="arrowHome" 
        src={arrow} 
        alt="scroll down" 
      /></a>
    </div>
  );
};

export default Home;
