import { useEffect } from "react";
import w_plane from "../svg/w_plane.svg";
import arrow from "../svg/arrow.svg";

const Home = () => {

  useEffect(() => {

    const plane = document.getElementById('planeHome'); 


    const handleScroll = () => {
      if(plane){
        const t = window.scrollY * 0.002; 

        const plane_x_translate = 300 * Math.cos(t) - 150;
        const plane_y_translate = 100 * Math.sin(2 * t);


        const plane_rotation = Math.atan2(
            200 * Math.cos(2 * t), // dérivée de y
            -300 * Math.sin(t)     // dérivée de x
        ) * (180 / Math.PI);

        // Application des transformations
        plane.style.transform = `translate(${plane_x_translate}px, ${plane_y_translate}px) rotate(${plane_rotation}deg)`;

      }

    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);



  return (
    <div className="home">

      <span className="blur_point" id="orange_blur_point"></span>
      <span className="blur_point" id="blue_blur_point"></span>

      <div className="home-header">
        <h1>Tancrède Gorand</h1>
        <p className="subtitle">INGÉNIERIE CRÉATIVE</p>
      </div>

      <img id="planeHome" src={w_plane} alt="Airplane illustration" />
      <a href="#about"><img 
        id="arrowHome" 
        src={arrow} 
        alt="scroll down" 
      /></a>
    </div>
  );
};

export default Home;
