import React from 'react';
import { useEffect, useRef, useState } from "react";
import data from '../data/data.json';

const Split1 = () => {
    return (
        <div className="split1">
            <span></span>
            <p>Cliquez pour découvrir</p>
            <span></span>
        </div>
    );
};

const Menu_page_item = ({ titre }) => {
    return (
        <div className='menu_page_item'>
            <span></span>
            <p>{titre}</p>
        </div>
    );
};

const Menu_page = () => {
    const menuRef = useRef(null);
    const [isVisible, setIsVisible] = useState(false);
    const [startY, setStartY] = useState(0);

    const sections = data.fr.sections;

    const carouselRefs = useRef(
        Object.fromEntries(sections.map(section => [section.titre, { current: null }]))
    );
    const velocityRefs = useRef(
        Object.fromEntries(sections.map(section => [section.titre, 0]))
    );

    useEffect(() => {
        const observer = new IntersectionObserver(
            (entries) => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        setIsVisible(true);
                        setStartY(window.scrollY);
                    } else {
                        setIsVisible(false);
                    }
                });
            },
            { threshold: 0.1 }
        );

        if (menuRef.current) {
            observer.observe(menuRef.current);
        }

        return () => {
            if (menuRef.current) {
                observer.unobserve(menuRef.current);
            }
        };
    }, []);

    useEffect(() => {
        if (!isVisible) return;

        let animationFrameId;
        let lastScrollY = window.scrollY;

        const smoothScroll = () => {
            const relativeScrollY = window.scrollY - startY;
            const maxScroll = 1000;
            const boundedScrollY = Math.max(0, Math.min(relativeScrollY, maxScroll));
            const scrollFactor = 0.2; // Facteur de défilement uniforme

            sections.forEach((section, index) => {
                const carousel = carouselRefs.current[section.titre].current;
                if (!carousel) return;

                const isMovingRight = index % 2 === 1;

                // Valeurs de base ajustées
                const baseOffset = isMovingRight ? 600 : -600; // Augmenté pour plus d'amplitude
                const direction = isMovingRight ? -1 : 1;

                // Position cible ajustée
              const initialOffset = isMovingRight ? -window.innerWidth * 0.5 : 0; // -50vw pour les carrousels qui vont à droite
const targetPosition = initialOffset + baseOffset + (boundedScrollY * scrollFactor * direction);
                const currentPosition = parseFloat(carousel.style.transform?.replace('translateX(', '').replace('px)', '') || 0);

                // Ajustement plus progressif de la vélocité
                velocityRefs.current[section.titre] += 
                    (targetPosition - currentPosition) * 0.08; // Réduit pour plus de fluidité

                // Amortissement ajusté
                velocityRefs.current[section.titre] *= 0.92; // Légèrement ajusté pour une meilleure réponse

                carousel.style.transform = `translateX(${velocityRefs.current[section.titre]}px)`;
            });

            // Seuil de mouvement ajusté
            const hasMovement = Object.values(velocityRefs.current).some(
                velocity => Math.abs(velocity) > 0.05 // Seuil réduit pour une animation plus complète
            );

            if (hasMovement) {
                animationFrameId = requestAnimationFrame(smoothScroll);
            }

            lastScrollY = window.scrollY;
        };


        const startSmoothScroll = () => {
            if (isVisible) {
                cancelAnimationFrame(animationFrameId);
                animationFrameId = requestAnimationFrame(smoothScroll);
            }
        };

        window.addEventListener('scroll', startSmoothScroll);

        const updateOpacity = (targetId) => {
            sections.forEach(section => {
                const carousel = carouselRefs.current[section.titre].current;
                if (carousel && carousel.id !== targetId) {
                    carousel.style.opacity = '0.7';
                }
            });
        };

        const resetOpacity = () => {
            sections.forEach(section => {
                const carousel = carouselRefs.current[section.titre].current;
                if (carousel) {
                    carousel.style.opacity = '1';
                }
            });
        };

        sections.forEach(section => {
            const carousel = carouselRefs.current[section.titre].current;
            if (carousel) {
                carousel.addEventListener('mouseover', () => updateOpacity(section.titre));
                carousel.addEventListener('mouseout', resetOpacity);
            }
        });

        return () => {
            window.removeEventListener('scroll', startSmoothScroll);
            cancelAnimationFrame(animationFrameId);
            sections.forEach(section => {
                const carousel = carouselRefs.current[section.titre].current;
                if (carousel) {
                    carousel.removeEventListener('mouseover', () => updateOpacity(section.titre));
                    carousel.removeEventListener('mouseout', resetOpacity);
                }
            });
        };
    }, [isVisible, startY, sections]);

    return (
        <div className="menu_page" ref={menuRef}>
            <Split1 />
            {sections.map((section, index) => (
                <React.Fragment key={section.titre}>
            <div 
                className="carrousel" 
                ref={el => {
                    carouselRefs.current[section.titre].current = el;
                    if (el && index % 2 === 1) {
                        el.style.transform = `translateX(-50vw)`;
                    }
                }}
                id={section.titre}
            >
                        {Array(12).fill(null).map((_, i) => (
                            <Menu_page_item key={i} titre={section.titre} />
                        ))}
                    </div>
                    {index < sections.length - 1 && <span className="split2" />}
                </React.Fragment>
            ))}
            <Split1 />
        </div>
    );
};

export default Menu_page;
