import { useState, useEffect } from 'react';

const Footer = () =>{

    const [dotPosition, setDotPosition] = useState({ x: 0, y: 0 });
    const [circlePosition, setCirclePosition] = useState({ x: 0, y: 0 });

    const delay = 0.1; // Ajuste ce délai pour modifier la vitesse du mouvement

    useEffect(() => {
        const handleMouseMove = (event) => {
        const mouseX = event.clientX;
        const mouseY = event.clientY;

        // Mettre à jour la position du dot
        setDotPosition((prevPosition) => ({
            x: prevPosition.x + (mouseX - prevPosition.x) * delay,
            y: prevPosition.y + (mouseY - prevPosition.y) * delay,
        }));

        // Mettre à jour la position du cercle
        setCirclePosition((prevPosition) => ({
            x: prevPosition.x + (mouseX - prevPosition.x) * (delay * 0.8), // Moins rapide pour le cercle
            y: prevPosition.y + (mouseY - prevPosition.y) * (delay * 0.8),
        }));
        };

        // Écouter les mouvements de la souris
        window.addEventListener('mousemove', handleMouseMove);

        // Nettoyer l'événement quand le composant est démonté
        return () => {
        window.removeEventListener('mousemove', handleMouseMove);
        };
    }, []);


      return (
        <div>
            <span id='dot'></span>
            <span id='circle'></span>
        </div> 
      );
};

export default Footer;
