import { useEffect } from "react";

const Footer = () => {
    useEffect(() => {
        const dot = document.getElementById('dot'); 
        const circle = document.getElementById('circle');

        const handleMouseMove = (event) => {
            const mouseX = event.clientX;
            const mouseY = event.clientY;

            dot.style.left = mouseX + 'px';
            dot.style.top = mouseY + 'px';

            setTimeout(() => {
                circle.style.left = mouseX + 'px';
                circle.style.top = mouseY + 'px';
            }, 40);
        };

        const handleMouseEnter = () => {
            circle.classList.add('circle_open');
        };

        const handleMouseLeave = () => {
            circle.classList.remove('circle_open');
        };

        window.addEventListener('mousemove', handleMouseMove);

        // Sélectionner tous les liens et leur ajouter les écouteurs d'événements
        const links = document.querySelectorAll('a');
        links.forEach(link => {
            link.addEventListener('mouseenter', handleMouseEnter);
            link.addEventListener('mouseleave', handleMouseLeave);
        });

        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
            links.forEach(link => {
                link.removeEventListener('mouseenter', handleMouseEnter);
                link.removeEventListener('mouseleave', handleMouseLeave);
            });
        };
    }, []);

    return (
        <div id="dot_container">
            <span className='dot' id="dot"></span>
            <span className='circle' id="circle"></span>
        </div>
    );
};

export default Footer;
