import { useEffect, useRef, useState } from "react";

const BannerTitleContent = ({ numItem, numTranslate, text }) => {
    const textRef = useRef(null);
    const [dynamicSpacing, setDynamicSpacing] = useState(210); // Valeur par défaut

    useEffect(() => {
        if (textRef.current) {
            const wordWidth = textRef.current.offsetWidth; // Récupère la largeur du texte
            setDynamicSpacing(wordWidth + 50); // Ajuste l'espacement basé sur la largeur
        }
    }, [text]); // Recalculer si le texte change

    return (
        <div
            className={`bannerTitleContent ${numItem} ${numTranslate}`}
            style={{ left: `max(calc(${dynamicSpacing}px * 10), 100%)` }} // Mise à jour dynamique
        >
            <span></span>
            <p ref={textRef}>{text}</p>
        </div>
    );
};


const BannerTitle = ({numTranslate}) => {
    const nbItem = 10; 
    return (
        <div className={`bannerTitle`}>
            {[...Array(nbItem)].map((_, index) => (
                <BannerTitleContent key={index} numItem={`item${index + 1}`} numTranslate={numTranslate} text="" />
            ))}
        </div>
    );
};

export default BannerTitle;
