import { useEffect, useState } from 'react';



const Menu_burger_list = () => {
    return (
        <div className='menu_list'>
        <ul>
        <li>Accueil</li>
        <li>DÃ©veloppement</li>
        <li>Son</li>
        <li>Visuel</li>
        </ul>
    </div>

    );

