import React, { useEffect, useRef } from "react";

// Styles CSS intégrés
const styles = {
  "@keyframes marquee": {
    "0%": { transform: "translateX(0%)" },
    "100%": { transform: "translateX(-100%)" }
  }
};

export const Test = () => {
    const marqueeContentRef = useRef(null);

    useEffect(() => {
      const marqueeContent = marqueeContentRef.current;
      if (marqueeContent) {
        const clone = marqueeContent.cloneNode(true);
        marqueeContent.parentElement?.appendChild(clone);
      }
    }, []);

    const containerStyle = {
      minHeight: "60px",
      background: "linear-gradient(to right, var(--goldgradient-dark), var(--goldgradient-light))",
      display: "flex",
      alignItems: "center",
      overflow: "hidden",
      gap: "5rem",
      borderRadius: "16px",
      whiteSpace: "nowrap",
      position: "relative",
      msOverflowStyle: "none",
      scrollbarWidth: "none"
    };

    const contentStyle = {
      display: "flex",
      alignItems: "center",
      gap: "5rem",
      color: "var(--grey-600)",
      animation: "marquee 20s linear infinite"
    };

    const largeTextStyle = {
      fontWeight: 800,
      fontSize: "30px",
      verticalAlign: "middle"
    };

    const specialFontStyle = {
      fontFamily: "PPNeueBit",
      fontSize: "40px"
    };

    return (
      <style>
        {`
          @keyframes marquee {
            0% { transform: translateX(0%); }
            100% { transform: translateX(-100%); }
          }

          .marquee {
            white-space: nowrap;
            overflow: hidden;
            position: relative;
          }

          .marquee-content {
            animation: marquee 20s linear infinite;
          }

          .no-scrollbar::-webkit-scrollbar {
            display: none;
          }

          .no-scrollbar {
            -ms-overflow-style: none;
            scrollbar-width: none;
          }
        `}
      </style>,
      <div style={containerStyle} className="marquee no-scrollbar">
        <div style={contentStyle} ref={marqueeContentRef} className="marquee-content">
          <div>
            <span style={largeTextStyle}>Parraine</span>{" "}
            <span style={specialFontStyle}>un ami</span>
          </div>
          <div>
            <span style={largeTextStyle}>Gagne des jours</span>{" "}
            <span style={specialFontStyle}>gratuits</span>
          </div>
          <div>
            <span style={largeTextStyle}>Parraine</span>{" "}
            <span style={specialFontStyle}>un ami</span>
          </div>
          <div>
            <span style={largeTextStyle}>Gagne des jours</span>{" "}
            <span style={specialFontStyle}>gratuits</span>
          </div>
          <div>
            <span style={largeTextStyle}>Parraine</span>{" "}
            <span style={specialFontStyle}>un ami</span>
          </div>
          <div>
            <span style={largeTextStyle}>Gagne des jours</span>{" "}
            <span style={specialFontStyle}>gratuits</span>
          </div>
        </div>
      </div>
    );
  };

export default Test;
