import React, { useState, useEffect, useRef } from 'react';

const Projects = ({section}) => {
    const [visibleProject, setVisibleProject] = useState(null);
    const projectRefs = useRef([]);

    useEffect(() => {
        const observers = [];

        projectRefs.current.forEach((ref, index) => {
            const observer = new IntersectionObserver(
                ([entry]) => {
                    if (entry.isIntersecting) {
                        setVisibleProject(section.projets[index].titre_projet);
                    }
                },
                {
                    threshold: 0.5 // Le projet est considéré visible quand 50% est visible
                }
            );

            if (ref) {
                observer.observe(ref);
                observers.push(observer);
            }
        });

        // Cleanup
        return () => {
            observers.forEach(observer => observer.disconnect());
        };
    }, [section.projets]);

    return (
        <div className="projects" id="projects">
            <div className="projects-summary">
                <span></span>
                <div>
                    {section.projets.map((projet, index) => (
                        <p 
                            key={index}
                            style={{
                                opacity: visibleProject === projet.titre_projet ? 1 : 0.5,
                                transition: 'opacity 0.3s ease'
                            }}
                        >
                            {projet.titre_projet}
                        </p>
                    ))}
                </div>
            </div>
            <div className="projects-list">
                {section.projets.map((project, index) => (
                    <div 
                        key={index}
                        ref={el => projectRefs.current[index] = el}
                    >
                        <Project 
                            project={project} 
                            titreSection={section.titre}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Projects;
