import { useEffect, useState } from 'react';


import Menu from './Menu';
import Footer from './Footer';

import arrow from '../svg/arrow.svg';

import data from '../data/data.json';



const BannerTitleContent = () => {
    return(
        <div className='bannerTitleContent'>
            <span></span>
            <p>DÃ©veloppement</p>
        </div>
    );
}; 


const BannerTitle = ({ translateClass }) => {
    const nb_components = 5;


    const [componentsArray, setComponentsArray] = useState(
        Array.from({ length: nb_components }, (_, index) => (
            <BannerTitleContent key={index} />
        ))
    );

    useEffect(() => {
        const intervalId = setInterval(() => {
            setComponentsArray((prevArray) => {
                const newArray = [...prevArray]; 
                const newComponent = newArray[3]; 
                newArray.pop(); 
                newArray.unshift(newComponent); 

                console.log(newArray); 
                return newArray;
            });
        }, 1200); 

        return () => clearInterval(intervalId); 
    }, []);

    return <div className={`bannerTitle ${translateClass}`}>{componentsArray}</div>;
};


const Category = () => {
    return(
        <div className='category'>
            <p>Vue.js</p>
        </div>
    ); 
}; 

const ProjectBanner = () => {
    return (
        <div className='project_banner'>
            <div className='titles_page'>
                <BannerTitle translateClass="translateTitle1"/>
                <BannerTitle translateClass="translateTitle2"/>
                <BannerTitle translateClass="translateTitle1"/>
            </div>
            <div className='categories_container'>
                <div className='categories'>
                    <Category/>
                    <Category/>
                    <Category/>
                    <Category/>
                </div>
                <div>
                    <a href="#fr"><img 
                    id="arrowHome" 
                    src={arrow} 
                    alt="scroll down" 
                    /></a>
                </div>

            </div>

        </div>
    ); 
}; 

const ProjectPage = () => {
    return (
        <div>
            <Menu />
            <ProjectBanner />
            <Footer />
        </div>
    );
};

export default ProjectPage;
